import React from 'react';
import { json, checkStatus } from './utils';

const Currency = (props) => {
  const {
    Rates
  } = props.currency;

  return (
    <div className="row">
      <div className="col-8 col-md-10 col-lg-11 mb-3">
        <p>{Rates}</p>
      </div>
    </div>
  )
}

class ExchangeRates extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      results: [],
      error: '',
    };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    event.preventDefault();
    fetch(`https://api.frankfurter.app/latest?from=USD`)
      .then(checkStatus)
      .then(json)
      .then((data) => {
        if (!data) {
          throw new Error(data.Error);
        }
        if (data) {
          console.log(data);
          //console.log(data.rates);
        //   Object.keys(data.rates).forEach(function(key) {
        //     console.log(key, data.rates[key]);
        // });
          this.setState({ results: data.rates, error: ''});
        }
      })
      .catch((error) => {
        this.setState({ error: error.message });
        console.log(error);
      })
  }

  render() {
    const { results, error } = this.state;

    return (
      <div className="container">
        <div className="row">
          <div className="col-12">
            <form onSubmit={this.handleSubmit} className="form-inline my-4">
              <button type="submit" className="btn btn-primary">Submit</button>
            </form>
            {(() => {
              if (error) {
                return error;
              }
              return results.map((currency) => {
                return <Currency key={currency} currency={currency} />;
              })
            })()}
          </div>
        </div>
      </div>
    )
  }
}

export default ExchangeRates;
