import React from 'react';
import { json, checkStatus } from './utils';
import DropdownMenu from './DropdownMenu';
import './CurrencyConverter.css';
import { FaArrowsAltH } from 'react-icons/fa';

class CurrencyConverter extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      results: [],
      error: '',
      chosenCurrency: 'USD',
      convertTo: 'EUR',
      amountToConvert: '',
      amountToConvert2: '',
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSwap = this.handleSwap.bind(this);
  }

  convertCurrencyTo = (currency) => {
    this.setState({ convertTo: currency});
  }

  chooseCurrency = (currency) => {
    this.setState({ chosenCurrency: currency});
  }

  handleSwap(event){
    event.preventDefault();
    let { chosenCurrency, convertTo } = this.state;
    this.setState({convertTo: chosenCurrency});
    this.setState({chosenCurrency: convertTo});
    fetch(`https://api.frankfurter.app/latest?from=${convertTo}`)
      .then(checkStatus)
      .then(json)
      .then((data) => {
        if (!data) {
          throw new Error(data.Error);
        }
        if (data) {
          console.log(data.rates);
          this.setState({ results: data.rates, error: ''});
        }
      })
      .catch((error) => {
        this.setState({ error: error.message });
        console.log(error);
      })
  }

  handleChange(event){
    this.setState({ amountToConvert: event.target.value });
    this.setState({ amountToConvert2: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    let { chosenCurrency } = this.state;
    fetch(`https://api.frankfurter.app/latest?from=${chosenCurrency}`)
      .then(checkStatus)
      .then(json)
      .then((data) => {
        if (!data) {
          throw new Error(data.Error);
        }
        if (data) {
          console.log(data.rates);
          this.setState({ results: data.rates, error: ''});
        }
      })
      .catch((error) => {
        this.setState({ error: error.message });
        console.log(error);
      })
  }

  render() {
    const { results, error, chosenCurrency, convertTo, amountToConvert, amountToConvert2 } = this.state;
    return (
      <div className="container">
        <form className="form-inline my-4" onSubmit={this.handleSubmit}>
          <div className="row">
            <h2>Exchange Rate Converter</h2>
            <div className="col-12 col-md-4">
                <h4>From</h4>
                <DropdownMenu value={chosenCurrency} passCurrency={this.chooseCurrency} />
                {(() => {
                  if (error) {
                    return error;
                  } if(chosenCurrency === convertTo){
                    return <input type="number" className="form-control" id="custom-form1" onChange={this.handleChange}defaultValue={amountToConvert2}/>
                  } else {
                    if(isNaN(results[chosenCurrency])){
                      return <input type="number" className="form-control" id="custom-form1" onChange={this.handleChange} defaultValue="" />;
                    } else {
                        return <input type="number" className="form-control" id="custom-form1" onChange={this.handleChange} defaultValue={(amountToConvert2*results[chosenCurrency]).toFixed(4)} />;
                    }
                  }
                })()}  
            </div>
            <div className="col-12 col-md-4 d-flex align-items-center justify-content-center">
              <button type="submit" className="btn btn-secondary mt-5 swap" onClick={this.handleSwap}>
                <FaArrowsAltH />
              </button>
            </div>
            <div className="col-12 col-md-4">
                <h4>To</h4>
                <DropdownMenu value={convertTo} passCurrency={this.convertCurrencyTo} />
                <div className="output">
                {(() => {
                  if (error) {
                    return error;
                  } if(chosenCurrency === convertTo){
                    return <input type="number" className="form-control" id="custom-form2" onChange={this.handleChange}defaultValue={amountToConvert}/>
                  } else {
                    if(isNaN(results[convertTo])){
                      return <input type="number" className="form-control" id="custom-form2" onChange={this.handleChange} defaultValue="" />;
                    } else {
                        return <input type="number" className="form-control" id="custom-form2" onChange={this.handleChange} value={(amountToConvert*results[convertTo]).toFixed(4)} />;
                    }
                  }
                })()}  
                </div> 
            </div>
          </div>
          <div className="row my-4 row-adjust">
            <button type="submit" className="btn btn-secondary btn-custom">Convert</button>
          </div>
        </form>
      </div>
    );
  };
}

export default CurrencyConverter;
