import React from 'react';
import { json, checkStatus } from './utils';
import DropdownMenu from './DropdownMenu';
import './CurrencyConverter.css';

class CurrencyConverter extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      results: [],
      error: '',
      chosenCurrency: 'USD',
      convertTo: 'USD'
    };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  convertCurrencyTo = (currency) => {
    this.setState({ convertTo: currency});
  }

  chooseCurrency = (currency) => {
    this.setState({ chosenCurrency: currency});
  }

  handleSubmit(event) {
    event.preventDefault();
    let { chosenCurrency } = this.state;;
    fetch(`https://api.frankfurter.app/latest?from=${chosenCurrency}`)
      .then(checkStatus)
      .then(json)
      .then((data) => {
        if (!data) {
          throw new Error(data.Error);
        }
        if (data) {
          console.log(data.rates);
          this.setState({ results: data.rates, error: ''});
        }
      })
      .catch((error) => {
        this.setState({ error: error.message });
        console.log(error);
      })
  }

  render() {
    const { results, error, chosenCurrency, convertTo } = this.state;
    return (
      <div className="container">
        <form className="form-inline my-4" onSubmit={this.handleSubmit}>
          <div className="row">
            <h2>Exchange Rate Converter</h2>
            <div className="col-12 col-md-6">
                <DropdownMenu passCurrency={this.chooseCurrency} />
                <input type="number" className="form-control" id="custom-form1" />
            </div>
            <div className="col-12 col-md-6">
                <DropdownMenu passCurrency={this.convertCurrencyTo} />
                <div className="px-2 pt-3" id="custom-form2">{convertTo}</div> 
            </div>
            <div className="row pl-0 my-4">
              <button type="submit" className="btn btn-secondary btn-custom mx-2">Submit</button>
            </div>
          </div>
        </form>
      </div>
    );
  };
}

export default CurrencyConverter;
